INSERT INTO books (title, author, genre, year, description, content) VALUES
('Мастер и Маргарита', 'Михаил Булгаков', 'Классика', 1967, 'Роман о дьяволе, который приезжает в Москву 1930-х годов вместе со свитой. Произведение переплетает две линии повествования: московскую современность и древний Ершалаим.', 'Полный текст романа "Мастер и Маргарита"...'),
('Преступление и наказание', 'Фёдор Достоевский', 'Классика', 1866, 'Психологический роман о студенте Родионе Раскольникове, который решается на убийство старухи-процентщицы. История о муках совести и искуплении.', 'Полный текст романа "Преступление и наказание"...'),
('Война и мир', 'Лев Толстой', 'Классика', 1869, 'Эпопея о русском обществе в эпоху войн против Наполеона. Переплетение судеб аристократических семей на фоне исторических событий.', 'Полный текст романа "Война и мир"...'),
('1984', 'Джордж Оруэлл', 'Фантастика', 1949, 'Антиутопия о тоталитарном обществе будущего, где правит Большой Брат и контролируется каждый шаг граждан. Роман-предупреждение о диктатуре.', 'Полный текст романа "1984"...'),
('Гарри Поттер и философский камень', 'Дж. К. Роулинг', 'Фэнтези', 1997, 'Первая книга о мальчике-волшебнике, который узнаёт о своём магическом наследии и поступает в школу чародейства и волшебства Хогвартс.', 'Полный текст книги "Гарри Поттер и философский камень"...'),
('Убить пересмешника', 'Харпер Ли', 'Драма', 1960, 'История о расовой несправедливости в американском городке, рассказанная глазами маленькой девочки. Классика о морали и человечности.', 'Полный текст романа "Убить пересмешника"...'),
('Граф Монте-Кристо', 'Александр Дюма', 'Приключения', 1844, 'Захватывающая история о моряке Эдмоне Дантесе, который был предан друзьями и провёл годы в тюрьме, а затем вернулся, чтобы отомстить.', 'Полный текст романа "Граф Монте-Кристо"...'),
('Анна Каренина', 'Лев Толстой', 'Классика', 1877, 'Роман о трагической любви замужней женщины и офицера, на фоне картины русского общества второй половины XIX века.', 'Полный текст романа "Анна Каренина"...');